    ]
    
    static var allList: [String] = {
        var questionAllList: [String] = []
        let questionCategoryList = Question.allCases
        
        questionCategoryList.forEach { questionCategory in
            if let question = list[questionCategory] {
                questionAllList += question
            }
        }
        return questionAllList
    }()
    
    static func randomQuestion(amount: Int) -> [String] {
        allList[randomPick: 3]
    }
